class City{constructor(t){this.sourceData=t;this.Data={province:[],city:[],area:[]};this.provinceKey=0;this.cityKey=0;this.areaKey=0;this.province()}set updateProvince(t){this.Data.province=t}set updateCity(t){this.Data.city=t}set updateArea(t){this.Data.area=t}getProvinceItem(t){let e=this.Data.province.filter(e=>e.code===t)[0];this.city(e.key);return e}getCityItem(t){let e=this.Data.city.filter(e=>e.code===t)[0];this.area(e.key);return e}getAreaItem(t){return this.Data.area.filter(e=>e.code===t)[0]}getFullAreaItem(t=0,e=0,i=0){return{provinceData:this.getProvinceItem(t),cityData:this.getCityItem(e),areaData:this.getAreaItem(i)}}province(t=0){this.provinceKey=t;this.updateProvince=this.setValue(this.sourceData[`${t}`]);this.updateCity=this.setValue(this.sourceData[`${t}_${t}`]);this.updateArea=this.setValue(this.sourceData[`${t}_${t}_${t}`])}city(t=0){this.cityKey=t;this.updateCity=this.setValue(this.sourceData[`${this.provinceKey}_${t}`]);this.updateArea=this.setValue(this.sourceData[`${this.provinceKey}_${t}_0`])}area(t=0){this.areaKey=t;this.updateArea=this.setValue(this.sourceData[`${this.provinceKey}_${this.cityKey}_${t}`])}setValue(t){if(Array.isArray(t)){return t.map((t,e)=>({value:t.split("-")[0],key:e,code:Number(t.split("-")[1])}))}else{return[]}}}export default City;